import { useState } from 'react';
import './styles.css';

function Square({ value, onSquareClick }) {
  return (
    <button className="square" onClick={onSquareClick}>
      {value}
    </button>
  );
}

function Board({ xIsNext, squares, onPlay }) {
  function handleClick(i) {
    if (squares[i] || calculateWinner(squares)) return;

    const nextSquares = squares.slice();
    const xCount = squares.filter(s => s === 'X').length;
    const oIndex = squares.findIndex(s => s === 'O');
    const xIndex = squares.findIndex(s => s === 'X');
    const isCorner = [0, 2, 6, 8].includes(i);
    const isCenterO = oIndex === 4;

    // First move by X: must be a corner
    if (xIsNext && xCount === 0) {
      if (!isCorner) {
        alert('Not a good choice!');
        return;
      } else {
        alert('Good choice!');
      }
    }

    // Second move by X: depends on O's first move
    if (xIsNext && xCount === 1) {
      if (isCenterO) {
        // X must choose the diagonal corner
        const diagonal = {
          0: 8,
          2: 6,
          6: 2,
          8: 0
        };
        if (diagonal[xIndex] !== i) {
          alert('Not a good choice!');
          return;
        } else {
          alert('Good choice!');
        }
      } else {
        // O is not in the center: check valid second corner
        const validMoves = [
          [0, 2], [0, 6],
          [2, 0], [2, 8],
          [6, 0], [6, 8],
          [8, 2], [8, 6]
        ];
        const valid = validMoves.some(([first, second]) => xIndex === first && i === second && squares[(first + second) / 2] !== 'O' && (first + second) / 2 !== 4);
        if (!valid) {
          alert('Not a good choice!');
          return;
        } else {
          alert('Good choice!');
        }
      }
    }

    nextSquares[i] = xIsNext ? 'X' : 'O';
    onPlay(nextSquares);
  }

  const winner = calculateWinner(squares);
  const status = winner
    ? 'Winner: ' + winner
    : 'Next player: ' + (xIsNext ? 'X' : 'O');

  return (
    <>
      <div className="status">{status}</div>
      <div className="board-row">
        <Square value={squares[0]} onSquareClick={() => handleClick(0)} />
        <Square value={squares[1]} onSquareClick={() => handleClick(1)} />
        <Square value={squares[2]} onSquareClick={() => handleClick(2)} />
      </div>
      <div className="board-row">
        <Square value={squares[3]} onSquareClick={() => handleClick(3)} />
        <Square value={squares[4]} onSquareClick={() => handleClick(4)} />
        <Square value={squares[5]} onSquareClick={() => handleClick(5)} />
      </div>
      <div className="board-row">
        <Square value={squares[6]} onSquareClick={() => handleClick(6)} />
        <Square value={squares[7]} onSquareClick={() => handleClick(7)} />
        <Square value={squares[8]} onSquareClick={() => handleClick(8)} />
      </div>
    </>
  );
}

export default function Game() {
  const [xIsNext, setXIsNext] = useState(true);
  const [squares, setSquares] = useState(Array(9).fill(null));

  function handlePlay(nextSquares) {
    setSquares(nextSquares);
    setXIsNext(!xIsNext);
  }

  return (
    <div className="game">
      <div className="game-board">
        <Board 
          xIsNext={xIsNext} 
          squares={squares} 
          onPlay={handlePlay} 
        />
      </div>
    </div>
  );
}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6], 
    [1, 4, 7], 
    [2, 5, 8],
    [0, 4, 8], 
    [2, 4, 6]
  ];
  for (let [a, b, c] of lines) {
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}
